import { useState } from "react"
import JoblyApi from "./api"

const SearchFilter = ({type,setCompanies}) => {
    const INITIALSTATE = {
        search: ''
    }
    const [formData,setFormData] = useState(INITIALSTATE)
    const handleChange = e => {
        const {name,value} = e.target
        setFormData(data => ({
            ...data,
            [name]: value
        }))
    }

    const handleSubmit = e => {
        e.preventDefault();
        const {search} = formData;
        setFormData(INITIALSTATE)
        const res = type = 'Companies' ? JoblyApi.filteredCompanies(search) : JoblyApi.filteredJobs(search)
    }
    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type='text' placeholder="Enter search term..." value={formData.search} name='search' onChange={handleChange}></input>
                <button>Search</button>
            </form>
        </div>
    )
}

export default SearchFilter
