import JoblyApi from "./api";


const ApplyButton = ({id,username}) => {
    const handleSubmit = async () => {
        await JoblyApi.apply(username,id)
    }
    return (
        <form onSubmit={handleSubmit}>
        <button>APPLY</button>
        </form>
    )
}

export default ApplyButton;
