import { useState,useEffect } from "react";
import './Profile.css'

const Profile = ({currentUser,setCurrentUser}) => {
    const INITIALSTATE = {
        username: '',
        first: '',
        last: '',
        email: ''
    }
    const [formData,setFormData] = useState(INITIALSTATE)
    const handleChange = e => {
        const {name,value} = e.target
        setFormData(data => ({
            ...data,
            [name]: value
        }))
    }

    const handleSubmit = async  e => {
        e.preventDefault();
        const {username,first,last,email} = formData;
        setFormData(INITIALSTATE)
    }
    return (
        <form className="profile-form"onSubmit={handleSubmit}>
            <div className="profile-containers">
                <label  htmlFor="username">Username</label>
                <input disabled type="text" onChange={handleChange} value={formData.username} placeholder={currentUser.username} name="username"/>
            </div>
            <div className="profile-containers">
                <label htmlFor="first">First Name</label>
                <input type="text" onChange={handleChange} value={formData.first} placeholder="first name" name="first"/>
            </div>
            <div className="profile-containers">
                <label htmlFor="last">Last Name</label>
                <input type="text" onChange={handleChange} value={formData.last} placeholder="last name" name="last"/>
            </div> 
            <div className="profile-containers">
                <label htmlFor="email">Email</label>
                <input type="email" onChange={handleChange} value={formData.email} placeholder="email" name="email"/>
            </div>
            <button>Update</button>
        </form>
    )
}   

export default Profile;
