        <div>
            {company ? <div><h1>{company[0].name}</h1> <p>{company[0].description}</p>{company[0].jobs.map(j => <JobCard job={j}/>)}</div> : <h1>Loading</h1>}


        </div>
    )
}

export default CompanyDetails;
